// Fun√ß√£o auxiliar para formatar tempo
export function formatarTempo(tempoEstimado) {
  if (tempoEstimado === '00:00') return 'Imediato';
  if (tempoEstimado.includes(':')) {
    const [horas, minutos] = tempoEstimado.split(':').map(Number);
    if (horas > 0) {
      return `${horas}h${minutos > 0 ? ` ${minutos}min` : ''}`;
    }
    return `${minutos} min`;
  }
  return tempoEstimado;
}

