import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import infermedicaService from '../services/infermedicaService';

const Triagem = () => {
  const [sintomas, setSintomas] = useState('');
  const [alergias, setAlergias] = useState('');
  const [intolerancia, setIntolerancia] = useState('');
  const [temAlergias, setTemAlergias] = useState(false);
  const [temIntolerancia, setTemIntolerancia] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [showAudioOption, setShowAudioOption] = useState(false);
  const [isProcessing, setIsProcessing] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioRef = useRef(null);
  const navigate = useNavigate();

  // Fun√ß√µes de grava√ß√£o de √°udio
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        setAudioUrl(URL.createObjectURL(blob));

        // Para o stream para liberar o microfone
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Erro ao acessar microfone:', error);
      alert('Erro ao acessar o microfone. Verifique as permiss√µes.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const playAudio = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  const cancelAudio = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    if (audioUrl) {
      URL.revokeObjectURL(audioUrl);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Valida se h√° sintomas (texto ou √°udio)
    if (!sintomas && !audioBlob) {
      alert('Por favor, descreva seus sintomas por texto ou √°udio.');
      return;
    }

    setIsProcessing(true);

    try {
      // Prepara dados do paciente para a IA
      const patientData = {
        sintomas: sintomas || 'Sintomas relatados por √°udio',
        alergias,
        intolerancia,
        idade: 30, // Valor padr√£o, pode ser coletado em formul√°rio expandido
        sexo: 'male' // Valor padr√£o, pode ser coletado em formul√°rio expandido
      };

      // Chama o servi√ßo de triagem com IA
      const resultadoTriagem = await infermedicaService.performTriage(patientData);

      // Salva os dados da triagem com resultado da IA
      const dadosTriagem = {
        sintomas,
        sintomasAudio: audioBlob ? '√Åudio gravado' : null,
        alergias,
        intolerancia,
        prioridade: resultadoTriagem.prioridade,
        tempoEstimado: resultadoTriagem.tempoEstimado,
        nivel: resultadoTriagem.nivel,
        confidence: resultadoTriagem.confidence,
        recomendacao: resultadoTriagem.recomendacao,
        sintomasSerios: resultadoTriagem.sintomasSerios,
        timestamp: new Date().toISOString(),
        processedByAI: true
      };

      localStorage.setItem('triagemData', JSON.stringify(dadosTriagem));

      // Mostra breve feedback sobre o resultado
      const corEmoji = {
        'vermelho': 'üö®',
        'laranja': '‚ö†Ô∏è', 
        'amarelo': '‚ö°',
        'verde': '‚úÖ',
        'azul': 'üíô'
      };

      alert(`${corEmoji[resultadoTriagem.prioridade]} Triagem conclu√≠da!\n\nN√≠vel: ${resultadoTriagem.nivel}\nRecomenda√ß√£o: ${resultadoTriagem.recomendacao}`);

      navigate('/ficha');

    } catch (error) {
      console.error('Erro na triagem:', error);
      alert('Erro ao processar triagem. Usando an√°lise b√°sica como backup.');

      // Fallback para an√°lise b√°sica em caso de erro
      const dadosTriagem = {
        sintomas,
        sintomasAudio: audioBlob ? '√Åudio gravado' : null,
        alergias,
        intolerancia,
        prioridade: 'verde',
        tempoEstimado: '40:00',
        timestamp: new Date().toISOString(),
        processedByAI: false
      };

      localStorage.setItem('triagemData', JSON.stringify(dadosTriagem));
      navigate('/ficha');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <div className="container">
      <div style={{ textAlign: 'center', marginBottom: '16px' }}>
        <img 
          src="/image.png" 
          alt="PulSafe Logo" 
          style={{
            width: '120px',
            height: 'auto',
            maxWidth: '100%'
          }}
        />
      </div>

      <div className="pulseira-badge">
        pulseira 607B
      </div>

      <div className="subtitle">
        triagem autom√°tica
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                ü§í
              </div>
              O que voc√™ est√° entindo?
            </span>
          </div>

          {/* Op√ß√µes de input */}
          <div style={{ display: 'flex', gap: '12px', marginBottom: '12px' }}>
            <button
              type="button"
              onClick={() => setShowAudioOption(false)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: !showAudioOption ? '#d13636' : '#e8ecef',
                color: !showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              ‚úèÔ∏è Texto
            </button>
            <button
              type="button"
              onClick={() => setShowAudioOption(true)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: showAudioOption ? '#d13636' : '#e8ecef',
                color: showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              üé§ √Åudio
            </button>
          </div>

          {!showAudioOption ? (
            <textarea
              className="form-input"
              placeholder="Descreva seus sintomas detalhadamente..."
              value={sintomas}
              onChange={(e) => setSintomas(e.target.value)}
              rows="3"
              style={{ resize: 'vertical', minHeight: '80px' }}
            />
          ) : (
            <div style={{
              border: '2px solid #e8ecef',
              borderRadius: '16px',
              padding: '20px',
              background: '#f8f9fa',
              textAlign: 'center'
            }}>
              {!audioBlob ? (
                <div>
                  {!isRecording ? (
                    <div>
                      <div style={{ fontSize: '48px', marginBottom: '16px' }}>üé§</div>
                      <p style={{ color: '#6c757d', marginBottom: '16px', fontSize: '14px' }}>
                        Toque para gravar seus sintomas
                      </p>
                      <button
                        type="button"
                        onClick={startRecording}
                        style={{
                          padding: '12px 24px',
                          background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer',
                          transition: 'transform 0.2s ease'
                        }}
                        onMouseDown={(e) => e.target.style.transform = 'scale(0.95)'}
                        onMouseUp={(e) => e.target.style.transform = 'scale(1)'}
                      >
                        üî¥ Iniciar Grava√ß√£o
                      </button>
                    </div>
                  ) : (
                    <div>
                      <div style={{ 
                        fontSize: '48px', 
                        marginBottom: '16px',
                        animation: 'pulse 1s infinite',
                        color: '#d13636'
                      }}>
                        üî¥
                      </div>
                      <p style={{ color: '#d13636', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                        Gravando... Fale agora!
                      </p>
                      <button
                        type="button"
                        onClick={stopRecording}
                        style={{
                          padding: '12px 24px',
                          background: '#6c757d',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer'
                        }}
                      >
                        ‚èπÔ∏è Parar Grava√ß√£o
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div>
                  <div style={{ fontSize: '48px', marginBottom: '16px', color: '#28a745' }}>‚úÖ</div>
                  <p style={{ color: '#28a745', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                    √Åudio gravado com sucesso!
                  </p>
                  <div style={{ display: 'flex', gap: '12px', justifyContent: 'center', flexWrap: 'wrap' }}>
                    <button
                      type="button"
                      onClick={playAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#28a745',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      ‚ñ∂Ô∏è Escutar
                    </button>
                    <button
                      type="button"
                      onClick={cancelAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      üóëÔ∏è Cancelar
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        cancelAudio();
                        startRecording();
                      }}
                      style={{
                        padding: '10px 20px',
                        background: '#ffc107',
                        color: '#212529',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      üîÑ Gravar Novo
                    </button>
                  </div>
                  <audio ref={audioRef} src={audioUrl} style={{ display: 'none' }} />
                </div>
              )}
            </div>
          )}
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #ff6b6b 0%, #ffa726 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                üíä
              </div>
              Tem alergias √† algum medicamento?
            </span>
          </div>

          <div style={{ display: 'flex', gap: '16px', marginBottom: '16px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px',
              cursor: 'pointer',
              padding: '8px 16px',
              borderRadius: '8px',
              background: temAlergias ? '#e8f5e8' : 'transparent',
              border: `2px solid ${temAlergias ? '#28a745' : '#e8ecef'}`,
              transition: 'all 0.3s ease'
            }}>
              <input
                type="checkbox"
                checked={temAlergias}
                onChange={(e) => {
                  setTemAlergias(e.target.checked);
                  if (!e.target.checked) {
                    setAlergias('');
                  }
                }}
                style={{ 
                  width: '18px', 
                  height: '18px',
                  accentColor: '#28a745'
                }}
              />
              <span style={{ fontWeight: '500', color: temAlergias ? '#28a745' : '#6c757d' }}>
                ‚úÖ Sim, tenho alergias
              </span>
            </label>
          </div>

          {temAlergias && (
            <textarea
              className="form-input"
              placeholder="Ex: Penicilina, Aspirina, Dipirona..."
              value={alergias}
              onChange={(e) => setAlergias(e.target.value)}
              rows="2"
              style={{ 
                resize: 'vertical', 
                minHeight: '60px',
                animation: 'fadeIn 0.3s ease',
                border: '2px solid #ff6b6b'
              }}
            />
          )}
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #4ecdc4 0%, #26a69a 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                ü•õ
              </div>
              Tem intoler√¢ncia √† algum alimento?
            </span>
          </div>

          <div style={{ display: 'flex', gap: '16px', marginBottom: '16px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px',
              cursor: 'pointer',
              padding: '8px 16px',
              borderRadius: '8px',
              background: temIntolerancia ? '#e8f5e8' : 'transparent',
              border: `2px solid ${temIntolerancia ? '#28a745' : '#e8ecef'}`,
              transition: 'all 0.3s ease'
            }}>
              <input
                type="checkbox"
                checked={temIntolerancia}
                onChange={(e) => {
                  setTemIntolerancia(e.target.checked);
                  if (!e.target.checked) {
                    setIntolerancia('');
                  }
                }}
                style={{ 
                  width: '18px', 
                  height: '18px',
                  accentColor: '#28a745'
                }}
              />
              <span style={{ fontWeight: '500', color: temIntolerancia ? '#28a745' : '#6c757d' }}>
                ‚úÖ Sim, tenho intoler√¢ncias
              </span>
            </label>
          </div>

          {temIntolerancia && (
            <textarea
              className="form-input"
              placeholder="Ex: lactose, gl√∫ten, nozes, frutos do mar..."
              value={intolerancia}
              onChange={(e) => setIntolerancia(e.target.value)}
              rows="2"
              style={{ 
                resize: 'vertical', 
                minHeight: '60px',
                animation: 'fadeIn 0.3s ease',
                border: '2px solid #4ecdc4'
              }}
            />
          )}
        </div>

        <button type="submit" className="btn-primary">
          <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
            üìã Enviar Triagem
          </span>
        </button>
      </form>
    </div>
  );
};

export default Triagem;
