import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { analisarTriagemIA, CORES_PRIORIDADE, formatarTempo } from '../utils/triagemAI';

const Triagem = () => {
  const [sintomas, setSintomas] = useState('');
  const [alergias, setAlergias] = useState('');
  const [intolerancia, setIntolerancia] = useState('');
  const [temAlergias, setTemAlergias] = useState(false);
  const [temIntolerancia, setTemIntolerancia] = useState(false);
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [showAudioOption, setShowAudioOption] = useState(false);
  const [analiseIA, setAnaliseIA] = useState(null);
  const [mostrarAnalise, setMostrarAnalise] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioRef = useRef(null);
  const navigate = useNavigate();

  // Funções de gravação de áudio
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        setAudioUrl(URL.createObjectURL(blob));

        // Para o stream para liberar o microfone
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Erro ao acessar microfone:', error);
      alert('Erro ao acessar o microfone. Verifique as permissões.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const playAudio = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  const cancelAudio = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    if (audioUrl) {
      URL.revokeObjectURL(audioUrl);
    }
  };

  // Função para analisar sintomas com IA
  const analisarSintomas = () => {
    if (!sintomas && !audioBlob) {
      alert('Por favor, descreva seus sintomas por texto ou áudio.');
      return;
    }

    // Analisa com a IA
    const resultado = analisarTriagemIA(
      sintomas, 
      temAlergias ? alergias : '', 
      temIntolerancia ? intolerancia : '', 
      !!audioBlob
    );

    setAnaliseIA(resultado);
    setMostrarAnalise(true);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Se ainda não foi analisado, faz a análise primeiro
    if (!analiseIA) {
      analisarSintomas();
      return;
    }

    // Salva os dados da triagem com a análise da IA
    const dadosTriagem = {
      sintomas,
      sintomasAudio: audioBlob ? 'Áudio gravado' : null,
      alergias: temAlergias ? alergias : '',
      intolerancia: temIntolerancia ? intolerancia : '',
      prioridade: analiseIA.prioridade,
      tempoEstimado: analiseIA.nivel.tempoEstimado,
      analiseCompleta: analiseIA,
      timestamp: new Date().toISOString()
    };

    localStorage.setItem('triagemData', JSON.stringify(dadosTriagem));
    navigate('/ficha');
  };

  return (
    <div className="container">
      <div style={{ textAlign: 'center', marginBottom: '16px' }}>
        <img 
          src="/image.png" 
          alt="PulSafe Logo" 
          style={{
            width: '120px',
            height: 'auto',
            maxWidth: '100%'
          }}
        />
      </div>

      <div className="pulseira-badge">
        pulseira 607B
      </div>

      <div className="subtitle">
        triagem automática
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                🤒
              </div>
              O que você está entindo?
            </span>
          </div>

          {/* Opções de input */}
          <div style={{ display: 'flex', gap: '12px', marginBottom: '12px' }}>
            <button
              type="button"
              onClick={() => setShowAudioOption(false)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: !showAudioOption ? '#d13636' : '#e8ecef',
                color: !showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              ✏️ Texto
            </button>
            <button
              type="button"
              onClick={() => setShowAudioOption(true)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: showAudioOption ? '#d13636' : '#e8ecef',
                color: showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              🎤 Áudio
            </button>
          </div>

          {!showAudioOption ? (
            <textarea
              className="form-input"
              placeholder="Descreva seus sintomas detalhadamente..."
              value={sintomas}
              onChange={(e) => setSintomas(e.target.value)}
              rows="3"
              style={{ resize: 'vertical', minHeight: '80px' }}
            />
          ) : (
            <div style={{
              border: '2px solid #e8ecef',
              borderRadius: '16px',
              padding: '20px',
              background: '#f8f9fa',
              textAlign: 'center'
            }}>
              {!audioBlob ? (
                <div>
                  {!isRecording ? (
                    <div>
                      <div style={{ fontSize: '48px', marginBottom: '16px' }}>🎤</div>
                      <p style={{ color: '#6c757d', marginBottom: '16px', fontSize: '14px' }}>
                        Toque para gravar seus sintomas
                      </p>
                      <button
                        type="button"
                        onClick={startRecording}
                        style={{
                          padding: '12px 24px',
                          background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer',
                          transition: 'transform 0.2s ease'
                        }}
                        onMouseDown={(e) => e.target.style.transform = 'scale(0.95)'}
                        onMouseUp={(e) => e.target.style.transform = 'scale(1)'}
                      >
                        🔴 Iniciar Gravação
                      </button>
                    </div>
                  ) : (
                    <div>
                      <div style={{ 
                        fontSize: '48px', 
                        marginBottom: '16px',
                        animation: 'pulse 1s infinite',
                        color: '#d13636'
                      }}>
                        🔴
                      </div>
                      <p style={{ color: '#d13636', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                        Gravando... Fale agora!
                      </p>
                      <button
                        type="button"
                        onClick={stopRecording}
                        style={{
                          padding: '12px 24px',
                          background: '#6c757d',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer'
                        }}
                      >
                        ⏹️ Parar Gravação
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div>
                  <div style={{ fontSize: '48px', marginBottom: '16px', color: '#28a745' }}>✅</div>
                  <p style={{ color: '#28a745', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                    Áudio gravado com sucesso!
                  </p>
                  <div style={{ display: 'flex', gap: '12px', justifyContent: 'center', flexWrap: 'wrap' }}>
                    <button
                      type="button"
                      onClick={playAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#28a745',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      ▶️ Escutar
                    </button>
                    <button
                      type="button"
                      onClick={cancelAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      🗑️ Cancelar
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        cancelAudio();
                        startRecording();
                      }}
                      style={{
                        padding: '10px 20px',
                        background: '#ffc107',
                        color: '#212529',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      🔄 Gravar Novo
                    </button>
                  </div>
                  <audio ref={audioRef} src={audioUrl} style={{ display: 'none' }} />
                </div>
              )}
            </div>
          )}
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #ff6b6b 0%, #ffa726 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                💊
              </div>
              Tem alergias à algum medicamento?
            </span>
          </div>

          <div style={{ display: 'flex', gap: '16px', marginBottom: '16px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px',
              cursor: 'pointer',
              padding: '8px 16px',
              borderRadius: '8px',
              background: temAlergias ? '#e8f5e8' : 'transparent',
              border: `2px solid ${temAlergias ? '#28a745' : '#e8ecef'}`,
              transition: 'all 0.3s ease'
            }}>
              <input
                type="checkbox"
                checked={temAlergias}
                onChange={(e) => {
                  setTemAlergias(e.target.checked);
                  if (!e.target.checked) {
                    setAlergias('');
                  }
                }}
                style={{ 
                  width: '18px', 
                  height: '18px',
                  accentColor: '#28a745'
                }}
              />
              <span style={{ fontWeight: '500', color: temAlergias ? '#28a745' : '#6c757d' }}>
                ✅ Sim, tenho alergias
              </span>
            </label>
          </div>

          {temAlergias && (
            <textarea
              className="form-input"
              placeholder="Ex: Penicilina, Aspirina, Dipirona..."
              value={alergias}
              onChange={(e) => setAlergias(e.target.value)}
              rows="2"
              style={{ 
                resize: 'vertical', 
                minHeight: '60px',
                animation: 'fadeIn 0.3s ease',
                border: '2px solid #ff6b6b'
              }}
            />
          )}
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #4ecdc4 0%, #26a69a 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                🥛
              </div>
              Tem intolerância à algum alimento?
            </span>
          </div>

          <div style={{ display: 'flex', gap: '16px', marginBottom: '16px' }}>
            <label style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px',
              cursor: 'pointer',
              padding: '8px 16px',
              borderRadius: '8px',
              background: temIntolerancia ? '#e8f5e8' : 'transparent',
              border: `2px solid ${temIntolerancia ? '#28a745' : '#e8ecef'}`,
              transition: 'all 0.3s ease'
            }}>
              <input
                type="checkbox"
                checked={temIntolerancia}
                onChange={(e) => {
                  setTemIntolerancia(e.target.checked);
                  if (!e.target.checked) {
                    setIntolerancia('');
                  }
                }}
                style={{ 
                  width: '18px', 
                  height: '18px',
                  accentColor: '#28a745'
                }}
              />
              <span style={{ fontWeight: '500', color: temIntolerancia ? '#28a745' : '#6c757d' }}>
                ✅ Sim, tenho intolerâncias
              </span>
            </label>
          </div>

          {temIntolerancia && (
            <textarea
              className="form-input"
              placeholder="Ex: lactose, glúten, nozes, frutos do mar..."
              value={intolerancia}
              onChange={(e) => setIntolerancia(e.target.value)}
              rows="2"
              style={{ 
                resize: 'vertical', 
                minHeight: '60px',
                animation: 'fadeIn 0.3s ease',
                border: '2px solid #4ecdc4'
              }}
            />
          )}
        </div>

        {/* Botão para analisar sintomas */}
        {!mostrarAnalise && (
          <button 
            type="button" 
            onClick={analisarSintomas}
            className="btn-primary"
            style={{ 
              marginBottom: '20px',
              background: 'linear-gradient(135deg, #007bff 0%, #0056b3 100%)'
            }}
          >
            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
              🤖 Analisar com IA
            </span>
          </button>
        )}

        {/* Resultado da análise da IA */}
        {mostrarAnalise && analiseIA && (
          <div style={{
            background: 'linear-gradient(135deg, #f8f9fa 0%, #e9ecef 100%)',
            border: `3px solid ${CORES_PRIORIDADE[analiseIA.prioridade]}`,
            borderRadius: '16px',
            padding: '24px',
            marginBottom: '20px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
          }}>
            {/* Cabeçalho da análise */}
            <div style={{
              textAlign: 'center',
              marginBottom: '20px',
              padding: '16px',
              background: CORES_PRIORIDADE[analiseIA.prioridade],
              borderRadius: '12px',
              color: 'white'
            }}>
              <div style={{ fontSize: '24px', marginBottom: '8px' }}>
                🎯 Análise da IA Concluída
              </div>
              <div style={{ fontSize: '20px', fontWeight: 'bold' }}>
                Prioridade: {analiseIA.nivel.significado.toUpperCase()}
              </div>
              <div style={{ fontSize: '16px', opacity: 0.9 }}>
                Tempo estimado: {formatarTempo(analiseIA.nivel.tempoEstimado)}
              </div>
            </div>

            {/* Detalhes da análise */}
            <div style={{ marginBottom: '16px' }}>
              <h4 style={{ color: '#333', marginBottom: '12px', fontSize: '16px' }}>
                📊 Principais Indicadores Encontrados:
              </h4>
              <div style={{ display: 'flex', flexWrap: 'wrap', gap: '8px', marginBottom: '12px' }}>
                {analiseIA.explicacao.principais_indicadores.map((indicador, index) => (
                  <span 
                    key={index}
                    style={{
                      background: '#e3f2fd',
                      color: '#1976d2',
                      padding: '4px 12px',
                      borderRadius: '20px',
                      fontSize: '12px',
                      border: '1px solid #bbdefb'
                    }}
                  >
                    {indicador}
                  </span>
                ))}
              </div>
            </div>

            {/* Recomendações */}
            <div style={{ marginBottom: '16px' }}>
              <h4 style={{ color: '#333', marginBottom: '12px', fontSize: '16px' }}>
                💡 Recomendações:
              </h4>
              <ul style={{ paddingLeft: '0', listStyle: 'none', margin: 0 }}>
                {analiseIA.recomendacoes.map((recomendacao, index) => (
                  <li key={index} style={{
                    padding: '8px 12px',
                    background: 'rgba(255,255,255,0.7)',
                    borderRadius: '8px',
                    marginBottom: '4px',
                    fontSize: '14px',
                    border: '1px solid #e0e0e0'
                  }}>
                    {recomendacao}
                  </li>
                ))}
              </ul>
            </div>

            {/* Confiabilidade */}
            <div style={{ 
              textAlign: 'center', 
              padding: '12px',
              background: 'rgba(255,255,255,0.8)',
              borderRadius: '8px',
              border: '1px solid #e0e0e0'
            }}>
              <span style={{ fontSize: '14px', color: '#666' }}>
                📈 Confiabilidade da análise: {Math.round(analiseIA.confiabilidade * 100)}%
              </span>
            </div>

            {/* Botão para nova análise */}
            <div style={{ textAlign: 'center', marginTop: '16px' }}>
              <button
                type="button"
                onClick={() => {
                  setAnaliseIA(null);
                  setMostrarAnalise(false);
                }}
                style={{
                  padding: '8px 16px',
                  background: '#6c757d',
                  color: 'white',
                  border: 'none',
                  borderRadius: '8px',
                  fontSize: '14px',
                  cursor: 'pointer'
                }}
              >
                🔄 Nova Análise
              </button>
            </div>
          </div>
        )}

        {/* Botão de envio - só aparece após análise */}
        {mostrarAnalise && analiseIA && (
          <button type="submit" className="btn-primary">
            <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
              📋 Confirmar e Enviar Triagem
            </span>
          </button>
        )}
      </form>
    </div>
  );
};

export default Triagem;
