import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Triagem = () => {
  const [sintomas, setSintomas] = useState('');
  const [alergias, setAlergias] = useState('');
  const [intolerancia, setIntolerancia] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [audioBlob, setAudioBlob] = useState(null);
  const [audioUrl, setAudioUrl] = useState(null);
  const [showAudioOption, setShowAudioOption] = useState(false);
  const mediaRecorderRef = useRef(null);
  const audioRef = useRef(null);
  const navigate = useNavigate();

  // Funções de gravação de áudio
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const mediaRecorder = new MediaRecorder(stream);
      mediaRecorderRef.current = mediaRecorder;

      const chunks = [];
      mediaRecorder.ondataavailable = (event) => {
        chunks.push(event.data);
      };

      mediaRecorder.onstop = () => {
        const blob = new Blob(chunks, { type: 'audio/wav' });
        setAudioBlob(blob);
        setAudioUrl(URL.createObjectURL(blob));

        // Para o stream para liberar o microfone
        stream.getTracks().forEach(track => track.stop());
      };

      mediaRecorder.start();
      setIsRecording(true);
    } catch (error) {
      console.error('Erro ao acessar microfone:', error);
      alert('Erro ao acessar o microfone. Verifique as permissões.');
    }
  };

  const stopRecording = () => {
    if (mediaRecorderRef.current && isRecording) {
      mediaRecorderRef.current.stop();
      setIsRecording(false);
    }
  };

  const playAudio = () => {
    if (audioRef.current) {
      audioRef.current.play();
    }
  };

  const cancelAudio = () => {
    setAudioBlob(null);
    setAudioUrl(null);
    if (audioUrl) {
      URL.revokeObjectURL(audioUrl);
    }
  };

    const handleSubmit = (e) => {
    e.preventDefault();

    // Valida se há sintomas (texto ou áudio)
    if (!sintomas && !audioBlob) {
      alert('Por favor, descreva seus sintomas por texto ou áudio.');
      return;
    }

    // Calcula a prioridade baseada nos sintomas informados
    let prioridade = 'verde'; // Baixa prioridade por padrão
    let tempoEstimado = '40:00';

    const sintomasTexto = sintomas.toLowerCase();

    // Lógica simples de triagem baseada em palavras-chave
    if (sintomasTexto.includes('dor no peito') || 
        sintomasTexto.includes('falta de ar') || 
        sintomasTexto.includes('desmaio') ||
        sintomasTexto.includes('sangramento')) {
      prioridade = 'vermelho';
      tempoEstimado = '05:00';
    } else if (sintomasTexto.includes('febre alta') || 
               sintomasTexto.includes('dor intensa') ||
               sintomasTexto.includes('vomito')) {
      prioridade = 'amarelo';
      tempoEstimado = '20:00';
    } else if (sintomasTexto.includes('febre') || 
               sintomasTexto.includes('dor') ||
               sintomasTexto.includes('tontura')) {
      prioridade = 'azul';
      tempoEstimado = '30:00';
    }

    // Salva os dados da triagem
    const dadosTriagem = {
      sintomas,
      sintomasAudio: audioBlob ? 'Áudio gravado' : null,
      alergias,
      intolerancia,
      prioridade,
      tempoEstimado,
      timestamp: new Date().toISOString()
    };

    localStorage.setItem('triagemData', JSON.stringify(dadosTriagem));
    navigate('/ficha');
  };

  return (
    <div className="container">
      <div style={{ textAlign: 'center', marginBottom: '16px' }}>
        <img 
          src="/image.png" 
          alt="PulSafe Logo" 
          style={{
            width: '120px',
            height: 'auto',
            maxWidth: '100%'
          }}
        />
      </div>

      <div className="pulseira-badge">
        pulseira 607B
      </div>

      <div className="subtitle">
        triagem automática
      </div>

      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                🤒
              </div>
              O que você está sentindo?
            </span>
          </div>

          {/* Opções de input */}
          <div style={{ display: 'flex', gap: '12px', marginBottom: '12px' }}>
            <button
              type="button"
              onClick={() => setShowAudioOption(false)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: !showAudioOption ? '#d13636' : '#e8ecef',
                color: !showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              ✏️ Texto
            </button>
            <button
              type="button"
              onClick={() => setShowAudioOption(true)}
              style={{
                padding: '8px 16px',
                borderRadius: '8px',
                border: 'none',
                background: showAudioOption ? '#d13636' : '#e8ecef',
                color: showAudioOption ? 'white' : '#6c757d',
                fontSize: '14px',
                fontWeight: '500',
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              🎤 Áudio
            </button>
          </div>

          {!showAudioOption ? (
            <textarea
              className="form-input"
              placeholder="Descreva seus sintomas detalhadamente..."
              value={sintomas}
              onChange={(e) => setSintomas(e.target.value)}
              rows="3"
              style={{ resize: 'vertical', minHeight: '80px' }}
            />
          ) : (
            <div style={{
              border: '2px solid #e8ecef',
              borderRadius: '16px',
              padding: '20px',
              background: '#f8f9fa',
              textAlign: 'center'
            }}>
              {!audioBlob ? (
                <div>
                  {!isRecording ? (
                    <div>
                      <div style={{ fontSize: '48px', marginBottom: '16px' }}>🎤</div>
                      <p style={{ color: '#6c757d', marginBottom: '16px', fontSize: '14px' }}>
                        Toque para gravar seus sintomas
                      </p>
                      <button
                        type="button"
                        onClick={startRecording}
                        style={{
                          padding: '12px 24px',
                          background: 'linear-gradient(135deg, #d13636 0%, #ff4757 100%)',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer',
                          transition: 'transform 0.2s ease'
                        }}
                        onMouseDown={(e) => e.target.style.transform = 'scale(0.95)'}
                        onMouseUp={(e) => e.target.style.transform = 'scale(1)'}
                      >
                        🔴 Iniciar Gravação
                      </button>
                    </div>
                  ) : (
                    <div>
                      <div style={{ 
                        fontSize: '48px', 
                        marginBottom: '16px',
                        animation: 'pulse 1s infinite',
                        color: '#d13636'
                      }}>
                        🔴
                      </div>
                      <p style={{ color: '#d13636', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                        Gravando... Fale agora!
                      </p>
                      <button
                        type="button"
                        onClick={stopRecording}
                        style={{
                          padding: '12px 24px',
                          background: '#6c757d',
                          color: 'white',
                          border: 'none',
                          borderRadius: '12px',
                          fontSize: '16px',
                          fontWeight: '600',
                          cursor: 'pointer'
                        }}
                      >
                        ⏹️ Parar Gravação
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                <div>
                  <div style={{ fontSize: '48px', marginBottom: '16px', color: '#28a745' }}>✅</div>
                  <p style={{ color: '#28a745', marginBottom: '16px', fontSize: '16px', fontWeight: '600' }}>
                    Áudio gravado com sucesso!
                  </p>
                  <div style={{ display: 'flex', gap: '12px', justifyContent: 'center', flexWrap: 'wrap' }}>
                    <button
                      type="button"
                      onClick={playAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#28a745',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      ▶️ Escutar
                    </button>
                    <button
                      type="button"
                      onClick={cancelAudio}
                      style={{
                        padding: '10px 20px',
                        background: '#dc3545',
                        color: 'white',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      🗑️ Cancelar
                    </button>
                    <button
                      type="button"
                      onClick={() => {
                        cancelAudio();
                        startRecording();
                      }}
                      style={{
                        padding: '10px 20px',
                        background: '#ffc107',
                        color: '#212529',
                        border: 'none',
                        borderRadius: '8px',
                        fontSize: '14px',
                        fontWeight: '600',
                        cursor: 'pointer'
                      }}
                    >
                      🔄 Gravar Novo
                    </button>
                  </div>
                  <audio ref={audioRef} src={audioUrl} style={{ display: 'none' }} />
                </div>
              )}
            </div>
          )}
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #ff6b6b 0%, #ffa726 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                💊
              </div>
              Tem alergia à algum medicamento?
            </span>
          </div>
          <textarea
            className="form-input"
            placeholder="Ex: Penicilina, Aspirina... ou 'Não tenho'"
            value={alergias}
            onChange={(e) => setAlergias(e.target.value)}
            rows="2"
            style={{ resize: 'vertical', minHeight: '60px' }}
          />
        </div>

        <div className="form-group">
          <div className="question-text">
            <span style={{ display: 'flex', alignItems: 'center', gap: '12px', marginBottom: '12px' }}>
              <div style={{ 
                fontSize: '24px',
                background: 'linear-gradient(135deg, #4ecdc4 0%, #26a69a 100%)',
                borderRadius: '12px',
                padding: '8px',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center'
              }}>
                🥛
              </div>
              Tem intolerância à algum alimento?
            </span>
          </div>
          <textarea
            className="form-input"
            placeholder="Ex: lactose, glúten, nozes... ou 'Não tenho'"
            value={intolerancia}
            onChange={(e) => setIntolerancia(e.target.value)}
            rows="2"
            style={{ resize: 'vertical', minHeight: '60px' }}
          />
        </div>

        <button type="submit" className="btn-primary">
          <span style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
            📋 Enviar Triagem
          </span>
        </button>
      </form>
    </div>
  );
};

export default Triagem;
