  // Diminuem urgência
  cronico: ['sempre', 'há muito tempo', 'crônico', 'já tinha', 'costuma ter'],
  leve: ['um pouco', 'levemente', 'às vezes', 'suave']
};

/**
 * Função principal de análise da IA
 * @param {string} sintomas - Descrição dos sintomas do paciente
 * @param {string} alergias - Informações sobre alergias
 * @param {string} intolerancia - Informações sobre intolerâncias
 * @param {boolean} temAudio - Se foi fornecido áudio (aumenta confiabilidade)
 * @returns {Object} Resultado da análise com prioridade e detalhes
 */
export function analisarTriagemIA(sintomas, alergias = '', intolerancia = '', temAudio = false) {
  // Normaliza o texto para análise
  const textoCompleto = `${sintomas} ${alergias} ${intolerancia}`.toLowerCase();

  // Inicializa pontuação para cada nível
  let pontuacao = {
    vermelho: 0,
    laranja: 0,
    amarelo: 0,
    verde: 0,
    azul: 0
  };

  // Análise por palavras-chave
  Object.keys(PALAVRAS_CHAVE).forEach(nivel => {
    PALAVRAS_CHAVE[nivel].forEach(palavra => {
      if (textoCompleto.includes(palavra)) {
        // Pontuação base pela palavra-chave
        pontuacao[nivel] += NIVEIS_PRIORIDADE[nivel.toUpperCase()].peso;

        // Bônus se a palavra aparece múltiplas vezes
        const ocorrencias = (textoCompleto.match(new RegExp(palavra, 'g')) || []).length;
        if (ocorrencias > 1) {
          pontuacao[nivel] += (ocorrencias - 1) * 0.5;
        }
      }
    });
  });

  // Aplicar modificadores
  aplicarModificadores(textoCompleto, pontuacao);

  // Bônus por ter áudio (indica mais detalhamento)
  if (temAudio) {
    // Aumenta levemente a confiabilidade, mas não altera drasticamente a prioridade
    Object.keys(pontuacao).forEach(nivel => {
      if (pontuacao[nivel] > 0) {
        pontuacao[nivel] += 0.2;
      }
    });
  }

  // Determina o nível de prioridade
  const nivelDeterminado = determinarNivelFinal(pontuacao, textoCompleto);

  // Gera explicação da análise
  const explicacao = gerarExplicacao(nivelDeterminado, pontuacao, textoCompleto);
  return {
    prioridade: nivelDeterminado.cor,
    nivel: nivelDeterminado,
    pontuacao,
    explicacao,
    confiabilidade: calcularConfiabilidade(pontuacao, textoCompleto.length, temAudio),
    recomendacoes: gerarRecomendacoes(nivelDeterminado, textoCompleto)
  };
}

/**
 * Aplica modificadores que podem alterar a urgência
 */
function aplicarModificadores(texto, pontuacao) {
  // REGRA ESPECIAL: Fraturas sempre são pelo menos LARANJA
  const fraturas = ['quebrada', 'quebrado', 'quebrou', 'fratura', 'osso quebrado'];
  const temFratura = fraturas.some(palavra => texto.includes(palavra));
  if (temFratura) {
    // Garante que fraturas tenham no mínimo pontuação laranja alta
    pontuacao.laranja = Math.max(pontuacao.laranja, 8);
    // Reduz drasticamente níveis menores
    pontuacao.verde *= 0.1;
    pontuacao.azul *= 0.05;
  }

  // Modificadores que aumentam urgência
  MODIFICADORES.idade_critica.forEach(mod => {
    if (texto.includes(mod)) {
      pontuacao.vermelho += 1;
      pontuacao.laranja += 0.5;
    }
  });
  MODIFICADORES.tempo_critico.forEach(mod => {
    if (texto.includes(mod)) {
      pontuacao.vermelho += 0.5;
      pontuacao.laranja += 0.3;
    }
  });
  MODIFICADORES.intensidade.forEach(mod => {
    if (texto.includes(mod)) {
      pontuacao.vermelho += 0.3;
      pontuacao.laranja += 0.5;
      pontuacao.amarelo += 0.2;
    }
  });

  // Modificadores que diminuem urgência (mas não para fraturas)
  if (!temFratura) {
    MODIFICADORES.cronico.forEach(mod => {
      if (texto.includes(mod)) {
        pontuacao.vermelho *= 0.5;
        pontuacao.laranja *= 0.7;
        pontuacao.amarelo *= 0.9;
        pontuacao.verde += 0.5;
        pontuacao.azul += 0.3;
      }
    });
    MODIFICADORES.leve.forEach(mod => {
      if (texto.includes(mod)) {
        pontuacao.vermelho *= 0.3;
        pontuacao.laranja *= 0.5;
        pontuacao.verde += 0.3;
        pontuacao.azul += 0.5;
      }
    });
  }
}

/**
 * Determina o nível final baseado na pontuação
 */
function determinarNivelFinal(pontuacao, texto) {
  // Encontra o nível com maior pontuação
  let maiorPontuacao = 0;
  let nivelEscolhido = 'azul';
  Object.keys(pontuacao).forEach(nivel => {
    if (pontuacao[nivel] > maiorPontuacao) {
      maiorPontuacao = pontuacao[nivel];
      nivelEscolhido = nivel;
    }
  });

  // Regras de segurança - se não há sintomas claros, assume não urgente
  if (maiorPontuacao === 0 || texto.trim().length < 10) {
    nivelEscolhido = 'azul';
  }

  // Regra de precaução - se há múltiplos indicadores de urgência
  if (pontuacao.vermelho > 1 && pontuacao.laranja > 1) {
    nivelEscolhido = 'vermelho';
  }
  return NIVEIS_PRIORIDADE[nivelEscolhido.toUpperCase()];
}

/**
 * Calcula a confiabilidade da análise
 */
function calcularConfiabilidade(pontuacao, tamanhoTexto, temAudio) {
  const somaPontuacao = Object.values(pontuacao).reduce((a, b) => a + b, 0);
  const detalhamento = Math.min(tamanhoTexto / 100, 1); // 0 a 1
  const audioBonus = temAudio ? 0.1 : 0;
  let confiabilidade = somaPontuacao / 10 + detalhamento + audioBonus;
  return Math.min(Math.max(confiabilidade, 0.3), 0.95); // Entre 30% e 95%
}

/**
 * Gera explicação da análise para transparência
 */
function gerarExplicacao(nivel, pontuacao, texto) {
  const indicadores = [];

  // Identifica os principais indicadores encontrados
  Object.keys(PALAVRAS_CHAVE).forEach(nivelNome => {
    PALAVRAS_CHAVE[nivelNome].forEach(palavra => {
      if (texto.includes(palavra) && pontuacao[nivelNome] > 0) {
        indicadores.push(`"${palavra}" (${nivelNome})`);
      }
    });
  });
  return {
    nivelDeterminado: `${nivel.cor.toUpperCase()} - ${nivel.significado}`,
    principais_indicadores: indicadores.slice(0, 5),
    // Top 5
    tempo_atendimento: nivel.tempo,
    justificativa: `Baseado na análise dos sintomas, foram identificados indicadores de prioridade ${nivel.cor}.`
  };
}

/**
 * Gera recomendações específicas baseadas no nível
 */
function gerarRecomendacoes(nivel, texto) {
  const recomendacoes = [];
  switch (nivel.cor) {
    case 'vermelho':
      recomendacoes.push('🚨 PROCURE ATENDIMENTO IMEDIATO');
      recomendacoes.push('🏥 Dirija-se ao pronto-socorro agora');
      recomendacoes.push('📞 Considere chamar ambulância (SAMU 192)');
      recomendacoes.push('⚠️ Não dirija sozinho se possível');
      break;
    case 'laranja':
      recomendacoes.push('🏥 Procure atendimento médico urgente');
      recomendacoes.push('⏰ Não aguarde mais de 10 minutos');
      recomendacoes.push('📱 Mantenha contato com familiar/responsável');
      break;
    case 'amarelo':
      recomendacoes.push('🏥 Procure atendimento médico');
      recomendacoes.push('⏰ Atendimento em até 50 minutos');
      recomendacoes.push('💊 Evite medicação por conta própria');
      break;
    case 'verde':
      recomendacoes.push('🏥 Atendimento médico recomendado');
      recomendacoes.push('⏰ Aguarde atendimento em até 2 horas');
      recomendacoes.push('💧 Mantenha-se hidratado');
      recomendacoes.push('😌 Permaneça calmo e descanse');
      break;
    case 'azul':
      recomendacoes.push('📋 Atendimento não urgente');
      recomendacoes.push('📅 Pode agendar consulta');
      recomendacoes.push('💡 Considere cuidados caseiros apropriados');
      break;
  }
  return recomendacoes;
}

// Função auxiliar para formatar tempo
export function formatarTempo(tempoEstimado) {
  if (tempoEstimado === '00:00') return 'Imediato';
  if (tempoEstimado.includes(':')) {
    const [horas, minutos] = tempoEstimado.split(':').map(Number);
    if (horas > 0) {
      return `${horas}h${minutos > 0 ? ` ${minutos}min` : ''}`;
    }
    return `${minutos} min`;
  }
  return tempoEstimado;
}

// Exporta cores para uso nos componentes
export const CORES_PRIORIDADE = {
  vermelho: '#d13636',
  laranja: '#ff6b35',
  amarelo: '#ffc107',
  verde: '#28a745',
  azul: '#007bff'
};

